INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`) VALUES
(1, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Se connecter à WooCommerce.com', 'Connectez-vous pour obtenir des notifications et des mises à jour importantes sur les produits.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-27 08:56:26', NULL, 0),
(2, 'wc-admin-welcome-note', 'info', 'en_US', 'Nouvelle(s) fonctionnalité(s)', 'Bienvenue dans la nouvelle expérience WooCommerce ! Cette nouvelle version vous offrira un aperçu de votre boutique dans le tableau de bord. Elle vous permettra également de gérer des aspects importants de votre activité (commandes, stock, avis, etc.) depuis n\'importe quelle partie de l\'interface et d\'explorer les données de votre boutique au moyen d\'une nouvelle section de statistiques et plus encore !', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-27 08:56:31', NULL, 0),
(3, 'wc-admin-store-notice-setting-moved', 'info', 'en_US', 'Vous recherchez le paramètre Annonce de la boutique ?', 'Vous le trouverez désormais dans l\'outil de personnalisation.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-27 08:56:32', NULL, 0),
(4, 'wc-admin-mobile-app', 'info', 'en_US', 'Installer l\'application mobile Woo', 'Installez l\'application mobile WooCommerce pour gérer les commandes, recevoir des notifications de vente et afficher les statistiques principales où que vous soyez.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-05-29 14:19:24', NULL, 0),
(5, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Astuces, mises à jour des produits et inspiration', 'Nous sommes à votre disposition. Profitez de conseils, de mises à jour des produits et de sources d\'inspiration directement dans votre boîte de messagerie.', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-05-29 14:19:26', NULL, 0),
(6, 'wc-admin-store-notice-giving-feedback', 'info', 'en_US', 'Donner votre avis sur votre expérience', 'Si vous aimez WooCommerce Admin, n\'hésitez pas à nous laisser une note de 5 étoiles. Un grand merci d\'avance !', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-06 08:47:43', NULL, 0),
(7, 'wc-admin-facebook-extension', 'info', 'en_US', 'Market on Facebook', 'Développez votre activité en ciblant la clientèle et en augmentant les ventes avec Facebook. Vous pouvez désormais installer cette extension gratuite.', 'thumbs-up', '{}', 'unactioned', 'woocommerce-admin', '2020-07-06 08:47:43', NULL, 0),
(8, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Aider WooCommerce à améliorer ses services grâce au suivi de l\'utilisation', 'Récupérer les données d\'utilisation nous permet d\'améliorer WooCommerce. Votre boutique sera prise en compte lors de l\'évaluation des nouvelles fonctionnalités et de la qualité d\'une mise à jour, ou pour déterminer l\'intérêt d\'une amélioration. Vous pouvez toujours consulter les <a href=\"http://creation2.info/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Paramètres</a> et choisir d\'interrompre le partage des données. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">En savoir plus</a> sur les données que nous collectons.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-06 08:47:44', NULL, 0);
